capabilities.setCapability(MobileCapabilityType.DEVICE_NAME,  Android Emulator );
capabilities.setCapability(MobileCapabilityType.BROWSER_NAME,  Chrome );  注意：在 4.4+ 版本的设备上，你也可以将  browserName  设置为'Browser' 在内建的浏览器上运行自动化。设置浏览器为'Chromium'是在所有设备可以运行的。在所有设备上，你可以将  browserName  设置为 'Chromium' 来对Chromium的某个版本进行自动化。

// javascript
// 假定我们已经为这个应用程序初始化了 `driver` 对象
driver
    .contexts().then(function (contexts) { // get list of available views. Returns array: [ NATIVE_APP , WEBVIEW_1 ]
        return driver.context(contexts[1]); // choose the webview context
    })

    // 执行一些 web 测试
    .elementsByCss('.green_button').click()

    .context('NATIVE_APP') // leave webview context

