  ```objectivec
  extern void _NSSetLogCStringFunction(void( )(const char  , unsigned, BOOL));  static void _GarbageFreeLogCString(const char *message, unsigned length, BOOL withSyslogBanner) {
     fprintf(stderr, "%s\n", message);
  }  int main (int argc, const char  argv[]) {
     NSAutoreleasePool  pool = [[NSAutoreleasePool alloc] init];
     int exitCode;   setbuf(stderr, NULL);

 _NSSetLogCStringFunction(_GarbageFreeLogCString);
 exitCode = WOApplicationMain(@"Application", argc, argv);
